struct Foo {
    virtual void fight() {
        ++fights;
        std::cout
            << "A foo fighter wins " << fights << " time(s)!"
            << std::endl;
    }
    int fights = 0;
};

struct Bar : Foo {
    void fight() override {
        ++fights;
        std::cout
            << "A bar fighter gets kicked out " << fights << " time(s)!"
            << std::endl;
    }
};

template <class T>
void reset(T& foo) {
    memset(&foo, 0, sizeof(foo));
}

void observe(Foo& foo) {
    foo.fight();
}

int main() {
    Foo     foo;    
    Bar     bar;

    observe(foo);   
    observe(bar);

    reset  (foo);     
    reset(bar);

    observe(foo);   
    observe(bar);
}

