fn enter_critical_section() {
    while LOCK.swap(true, Ordering::Acquire) {}
}

fn exit_critical_section() {
     // Downgraded to Relaxed
    LOCK.store(false, Ordering::Relaxed);
}

fn worker_thread() {
    enter_critical_section();
    unsafe {
        let val = COUNTER; 
        COUNTER = val + 1;
    }
    exit_critical_section();
}

