// Pseudocode!
struct PendingSend {
    std::array<std::byte, WireFormat::ChatMessageMaxSize> Data;
    size_t Length{};
    size_t Offset{};
};

struct ChatMessage {
    size_t SenderId;
    std::uintptr_t ClientId;
    std::string Message;
};

void ChatClient::Enqueue(const PendingSend& pending) {
    // Place on queue.
}

bool ChatClient::SendChatMessage(const ChatMessage& chat) {
    std::byte buffer[WireFormat::ChatMessageMaxSize];
    const size_t size = Serializer::ChatMessage(buffer, sizeof(buffer), chat);

    if (!size) {
        return false;
    }

    const size_t sent = send(buffer, size); // send exists on the base class.
    if (sent >= 0 && sent < size) {
        PendingSend pending;
        std::memcpy(pending.Data.data(), buffer + sent, size - sent);
        pending.Length = size - sent;
        Enqueue(pending);
    }
}

