#include <thread>

static constexpr size_t align_size = 
  std::hardware_destructive_interference_size;

struct alignas(align_size) localCount
{
    size_t val{ 0 };
};

int main()
{
    const auto n = 1'000;
    const auto thread_count = std::thread::hardware_concurrency();
    localCount local_count[thread_count];
    std::atomic<size_t> total_count{ 0 };

    std::vector<std::thread> workers;
    for (int i = 0; i < thread_count; ++i)
    {
        workers.emplace_back([&, index = i] {
            auto& my_int = local_count[index];
            for (int i = 0; i < n; ++i)
                ++my_int.val;

            total_count.fetch_add(my_int.val, std::memory_order_relaxed);
        });
    }

    for (auto &t : workers)
        t.join();

    std::cout << "Total count: " << total_count.load() << std::endl;
}

