class Wizard {
private:
    std::string name;

public:
    Wizard(std::string name) : name(name) {}

    void castFireball() {
        std::cout << name << " casts 🔥 Fireball!\n";
    }

    void castHeal() {
        std::cout << name << " casts 💖 Heal!\n";
    }

    void defend() {
        std::cout << name << " raises a 🛡️ Magic Shield!\n";
    }
};

class Action {
public:
    virtual void execute() = 0;
    virtual ~Action() = default;
};

// Concrete Actions
class FireballAction : public Action {
private:
    Wizard& wizard;

public:
    FireballAction(Wizard& wiz) : wizard(wiz) {}
    void execute() override {
        wizard.castFireball();
    }
};

class HealAction : public Action {
private:
    Wizard& wizard;

public:
    HealAction(Wizard& wiz) : wizard(wiz) {}
    void execute() override {
        wizard.castHeal();
    }
};

class DefendAction : public Action {
private:
    Wizard& wizard;

public:
    DefendAction(Wizard& wiz) : wizard(wiz) {}
    void execute() override {
        wizard.defend();
    }
};

// Invoker: handles Action queue
class ActionQueue {
private:
    std::queue<std::shared_ptr<Action>> actions;

public:
    void addAction(std::shared_ptr<Action> cmd) {
        actions.push(cmd);
    }

    void executeAll() {
        while (!actions.empty()) {
            actions.front()->execute();
            actions.pop();
        }
    }
};

int main() {
    Wizard merlin("Merlin");

    auto fireball = std::make_shared<FireballAction>(merlin);
    auto heal = std::make_shared<HealAction>(merlin);
    auto defend = std::make_shared<DefendAction>(merlin);

    ActionQueue queue;
    queue.addAction(fireball);
    queue.addAction(heal);
    queue.addAction(defend);

    queue.executeAll();
}

