class SparkEffect {
public:
    void render(int x, int y, float direction, float duration) const {
        std::cout << "Rendering Spark at (" << x << ", " << y
                  << ") dir=" << direction
                  << "s dur=" << duration
                  << "s [Shared: color=blue, sprite=Spark.png]" << std::endl;
    }
};

class SparkEffectFactory {
private:
    std::shared_ptr<SparkEffect> sharedSpark_;

public:
    std::shared_ptr<SparkEffect> getSparkEffect() {
        if (!sharedSpark_) {
            sharedSpark_ = std::make_shared<SparkEffect>();
            std::cout << "[Factory] Created shared SparkEffect.\n";
        }
        return sharedSpark_;
    }
};

// Extrinsic wrapper: represents a single spark instance
class SparkInstance {
private:
    int x_, y_;
    float direction_;
    float duration_;
    std::shared_ptr<SparkEffect> effect_;

public:
    SparkInstance(int x, int y, float dir, float dur, std::shared_ptr<SparkEffect> fx)
        : x_(x), y_(y), direction_(dir), duration_(dur), effect_(fx) {}

    void render() const {
        effect_->render(x_, y_, direction_, duration_);
    }
};

int main() {
    SparkEffectFactory factory;
    std::vector<SparkInstance> sparks;

    // Create 5 spark effects
    for (int i = 0; i < 5; ++i) {
        int x = i * 10;
        int y = i * 5;
        float direction = i * 15.0f;
        float duration = 1.0f + i * 0.1f;

        sparks.emplace_back(x, y, direction, duration, factory.getSparkEffect());
    }

    for (const auto& spark : sparks) {
        spark.render();
    }
}

