class Character {
private:
    std::string name;
    int hp;

public:
    Character(std::string name, int hp) : name(name), hp(hp) {}

    void takeDamage(int amount) {
        hp -= amount;
        if (hp < 0) hp = 0;
        std::cout << "[" << name << "] took " << amount << " damage! HP left: " << hp << "\n";
    }

    int getHP() const { return hp; }
};

class DamageHandler {
protected:
    std::shared_ptr<DamageHandler> next;

public:
    void setNext(std::shared_ptr<DamageHandler> nextHandler) {
        next = nextHandler;
    }

    virtual void handle(int& damage, Character& target) {
        if (next) {
            next->handle(damage, target);
        }
    }

    virtual ~DamageHandler() = default;
};

// Concrete Handlers

class MagicShield : public DamageHandler {
public:
    void handle(int& damage, Character& target) override {
        if (damage > 0) {
            std::cout << "[Magic Shield] Absorbed 10 damage.\n";
            damage -= 10;
            if (damage < 0) damage = 0;
        }
        DamageHandler::handle(damage, target);
    }
};

class Armor : public DamageHandler {
public:
    void handle(int& damage, Character& target) override {
        if (damage > 0) {
            std::cout << "[Armor] Reduced damage by 5.\n";
            damage -= 5;
            if (damage < 0) damage = 0;
        }
        DamageHandler::handle(damage, target);
    }
};

class BuffResistance : public DamageHandler {
public:
    void handle(int& damage, Character& target) override {
        if (damage > 0) {
            std::cout << "[Buff] Resisted 20% of remaining damage.\n";
            damage = static_cast<int>(damage * 0.8f);
        }
        DamageHandler::handle(damage, target);
    }
};

class FinalHealthHandler : public DamageHandler {
public:
    void handle(int& damage, Character& target) override {
        target.takeDamage(damage);
    }
};

int main() {
    Character hero("Melvin, The Intern", 1);

    auto magicShield = std::make_shared<MagicShield>();
    auto armor = std::make_shared<Armor>();
    auto buff = std::make_shared<BuffResistance>();
    auto healthHandler = std::make_shared<FinalHealthHandler>();

    magicShield->setNext(armor);
    armor->setNext(buff);
    buff->setNext(healthHandler);

    int incomingDamage = 5'000'000; // Negative feedback given to candidate

    magicShield->handle(incomingDamage, hero);
}

