class CutsceneManager {
public:
    void playIntro() {
        std::cout << "[Cutscene] A crack splits the sky...";
    }
};

class MusicSystem {
public:
    void playBossTheme() {
        std::cout << "[Music] Playing: 'The Cracked One' Theme";
    }
};

class EnvironmentController {
public:
    void darkenSky() {
        std::cout << "[Environment] The sky grows dark.";
    }
};

class BossAI {
public:
    void activatePhaseOne() {
        std::cout << "[Boss AI] The Cracked One begins its attack!";
    }

    void activatePhaseTwo() {
        std::cout << "[Boss AI] The Cracked One cracks open.";
        std::cout << "[Boss AI] Identifying The Intern for execution.";
    }
};

class HealthSystem {
public:
    void initializeHealth(int hp) {
        std::cout << "[Health] The Cracked One appears with " << hp << " HP!";
    }
};


class BossBattle {
private:
    CutsceneManager cutscene;
    MusicSystem music;
    EnvironmentController environment;
    BossAI ai;
    HealthSystem health;

public:
    void startBattle() {
        cutscene.playIntro();
        environment.darkenSky();
        music.playBossTheme();
        health.initializeHealth(10000);
        ai.activatePhaseOne();
    }

    void triggerPhaseTwo() {
        music.playBossTheme();
        ai.activatePhaseTwo();
    }

    void initiate() {
        std::cout << "== Initiating Final Encounter ==" << std::endl;
        bossBattle.startBattle();

        // Simulate battle...
        std::cout << "(Player fights...)" << std::endl;

        // Later in battle, transition to Phase 2
        bossBattle.triggerPhaseTwo();
    }
};

int main() {
    BossBattle bossBattle;
    bossBattle.initiate();
}

