    std::mt19937 gen(rd());
    std::uniform_real_distribution<> dis(0.0, 1.0);
    double randomValue = dis(gen);
    return randomValue;
}

// Method may or may not be called on boss' death.
std::shared_ptr<EnhancedWeapon> getCrackedBossAxeDrop(double roll) {
    auto* axe = new Axe();
    if (roll >= 0.99) {
        return std::make_shared<ExaltedWeapon>(axe);
    } else if (roll >= 0.9) {
        return std::make_shared<RareWeapon>(axe);
    } else {
        return std::make_shared<BasicWeapon>(axe);
    }
}

