public:
    void attack() override {
        std::cout << "Archer fires an arrow!" << std::endl;
    }
};

class Party : public Unit {
private:
    std::vector<std::shared_ptr<Unit>> members;

public:
    void add(const std::shared_ptr<Unit>& unit) {
        members.push_back(unit);
    }

    void disband() {
        members.clear();
    }

    void attack() override {
        if (members.empty())
            return;

        std::cout << "[Party begins attack!]" << std::endl;
        for (const auto& member : members) {
            member->attack();
        }

        std::cout << "[Party ends attack.]" << std::endl;
    }
};

int main() {
    // Units spawn
    auto knight1 = std::make_shared<Knight>();
    auto archer1 = std::make_shared<Archer>();
    auto knight2 = std::make_shared<Knight>();

    // User groups the units together
    auto smallParty = std::make_shared<Party>();
    smallParty->add(knight1);
    smallParty->add(archer1);
    smallParty->add(knight2);

    // Execute attack
    smallParty->attack();
}

