        std::cout << "Dragon takes flight!" << std::endl;
    }
};

// Dragon joins the army.
class DragonWarrior : public Warrior {
private:
    Dragon* dragon;

public:
    DragonWarrior(Dragon* d) : dragon(d) {}

    void attack() override {
        dragon->fly();
        dragon->breatheFire();
    }
};

// Client code for battle!
void battle(Warrior* warrior) {
    warrior->attack();
}

int main() {
    Knight knight;
    Dragon dragon;
    DragonWarrior dragonWarrior(&dragon);

    std::vector<Warrior*> army = { &knight, &dragonWarrior };

    for (Warrior* fighter : army) {
        battle(fighter);
    }
}

